function Coordenadas() {
  const [posicion, setPosicion] = useState(null);

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (pos) => {
        setPosicion([pos.coords.latitude, pos.coords.longitude]);
      },
      (error) => console.log(error)
    );
  }, []);

  const enviarCoordenadas = () => {
    if (!posicion) {
      console.log('Posici贸n desconocida');
      return;
    }
    
    fetch('http://127.0.0.1:8000/coordenada', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        latitud: posicion[0],
        longitud: posicion[1]
      })
    })
    .then(response => response.json())
    .then(data => {
      console.log(data);
    })
    .catch(error => {
      console.error(error);
    });
  }

  return (
    <div className="mapa">
      <h2>Mi ubicaci贸n actual:</h2>
      {posicion ? (
        <>
          <p>Latitud: {posicion[0]}</p>
          <p>Longitud: {posicion[1]}</p>
          <button onClick={enviarCoordenadas}>Enviar coordenadas</button>
        </>
      ) : (
        <p>Buscando tu ubicaci贸n...</p>
      )}
    </div>
  );
}
export default Coordenadas
