// Coordenadas.js

import { useEffect, useState } from "react";

function Coordenadas() {
  const [posicion, setPosicion] = useState([0, 0]);

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (pos) => {
        setPosicion([pos.coords.latitude, pos.coords.longitude]);
      },
      (error) => console.log(error)
    );
  }, []);

  const enviarCoordenadas = () => {
    fetch('http://127.0.0.1:8000/coordenadas', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        latitud: posicion[0],
        longitud: posicion[1]
      })
    })
    .then(response => response.json())
    .then(data => {
      console.log(data);
    })
    .catch(error => {
      console.error(error);
    });
  }

  return (
    <div className="mapa">
      <h2>Mi ubicaci√≥n actual:</h2>
      <p>Latitud: {posicion[0]}</p>
      <p>Longitud: {posicion[1]}</p>
      <button onClick={enviarCoordenadas}>Enviar coordenadas</button>
    </div>
  );
}

export default Coordenadas;
