import React, { useState, useEffect } from "react";
import axios from 'axios';


function Coordenadas() {
  const [posicion, setPosicion] = useState(null);

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (pos) => {
        setPosicion([pos.coords.latitude, pos.coords.longitude]);
      },
      (error) => console.log(error)
    );
  }, []);

 // funci贸n para enviar datos
 
 function enviarCoordenadas(Mapa) {
  axios.post('http://127.0.0.1:8000/coordenada/', Mapa, {
      headers: {
          "Content-Type": "application/json",
      }
  })
      .then((response) => {
          console.log(response.data);
      })
      .catch((error) => {
          console.error(error);
      }, []);
}
  

  return (
    
    <div className="mapa">
      <h2>Mi ubicaci贸n actual:</h2>
      {posicion ? (
        <>
          <p>Latitud: {posicion[0]}</p>
          <p>Longitud: {posicion[1]}</p>
          <button onClick={enviarCoordenadas}>Enviar coordenadas</button>
        </>
      ) : (
        <p>Buscando tu ubicaci贸n...</p>
      )}
    </div>
  );
}
export default Coordenadas
