import React, { Component } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import icono from '../images/iconoUbicacion.svg'

class Map extends Component {

    
  componentDidMount() {

    const customIcon = L.icon({
        iconUrl: icono,
        iconSize: [32, 32], // tamaño del ícono en píxeles
        iconAnchor: [16, 32] // posición del ancla del ícono en relación con su parte inferior izquierda
      });
      
    if (!this.map) {
      this.map = L.map('map').setView([51.505, -0.09], 13);
      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: 'Map data &copy; OpenStreetMap contributors'
      }).addTo(this.map);

      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(
          position => {
            const { latitude, longitude } = position.coords;
            L.marker([latitude, longitude], { icon: customIcon }).addTo(this.map);
            this.map.setView([latitude, longitude], 13);
          },
          error => {
            console.log(error);
          }
        );
      }
    }
  }

  render() {
    return (
      <div id="map" style={{ height: '400px' }}></div>
    );
  }
}

export default Map;
